mapper.select:
		SELECT t1.* ,
		t2.code_name AS code_name
		FROM t_stock t1
		LEFT JOIN m_code
		t2 ON t1.unit_id = t2.code_id
		
			t1.del_flg = 0 AND t2.code_kbn = 'A01'

			
				AND t1.name = #{name}
			

			
				AND t1.unit_id = #{unitId}
			

			
				AND t1.update_date >= #{startDate}
			

			
				AND t1.update_date <= #{endDate}
